import React, {useContext} from 'react';
import {AppContext} from "../App";
function Letter({letterPos, attemptValue}){
    const {board, curAttempt, word} = useContext(AppContext); 
    const letter = board[attemptValue][letterPos];
    if((attemptValue<= curAttempt.attempt) && (word.word.includes({letter}))){
        return <div className ="letter" id= "correct"> {letter}</div>
    }
    return <div className ="letter" id= "incorrect"> {letter}</div>

}
export default Letter;
