import React, {useContext} from 'react';
import {AppContext} from "../App";
function Letter({letterPos, attemptValue}){
    const {board, curAttempt, word, correct, almost, error, setCorrect,setAlmost,setError} = useContext(AppContext); 
    const letter = board[attemptValue][letterPos];


    if(attemptValue < curAttempt.attempt){
        if(word.word[letterPos]===letter){
            setCorrect({...correct, correct_set: new Set(correct.correct_set).add(letter)})
            if(almost.almost_set.has(letter)){
                setAlmost({...almost, almost_set: new Set(almost.almost_set).delete(letter)})
            }
            return <div className ="letter" id= "correct"> {letter}</div>
        }
        else if(word.word.slice(letterPos).includes(""+letter)){
            setAlmost({...almost,almost_set: new Set(almost.almost_set).add(letter)})
            return <div className ="letter" id= "almost"> {letter}</div>
        }
        else{
            setError({...error, error_set: new Set(error.error_set).add(letter)})
            return <div className ="letter" id= "error"> {letter}</div>
        }
    }
    return <div className ="letter" id> {letter}</div>

}
export default Letter;
