import React, {useContext} from "react";
import {AppContext} from "../App"
function Key({keyValue, bigKey}){
    const {board, setBoard, curAttempt,setCurrAttempt, word, correct, almost, error} = useContext(AppContext);
    const selector  = () =>{
        const newBoard = [...board];
        if (keyValue == "Delete" && curAttempt.letterPos==0){
            ///Try to delete at 0
            newBoard[curAttempt.attempt][curAttempt.letterPos] = ""
            setBoard(newBoard)
        }
        else if (keyValue == "Delete" && curAttempt.letterPos!=0){
            //Deleteing any other time
            newBoard[curAttempt.attempt][curAttempt.letterPos] = ""
            setCurrAttempt({...curAttempt, letterPos:curAttempt.letterPos-1});
            setBoard(newBoard);
        }
        else if(keyValue == "Enter" && curAttempt.letterPos!=5){
            // Not a complete word
            return;
        }
        else if(keyValue == "Enter" && curAttempt.letterPos==5){
            // Makeing a guess
            setCurrAttempt({attempt: curAttempt.attempt+1, letterPos:0});
            setBoard(newBoard);
            // need to check the guess 
        }
        else if(keyValue != "Enter" && curAttempt.letterPos==5){
            // Fully Spelt Word
            return;
        }
        else{
            //Entering a new key 
            newBoard[curAttempt.attempt][curAttempt.letterPos] = keyValue;
            setCurrAttempt({...curAttempt, letterPos:curAttempt.letterPos+1});
            setBoard(newBoard)
        }
    }
    ///
    return <button className= "d-inline-flex p-2 flex-shrink key" id = {bigKey && "big"} onClick={selector}>{keyValue}</button>
    }
export default Key;
