import './App.css';
import Board from "./Component/Board";
import React, { useState, createContext } from 'react';
import Keyboard from "./Component/KeyBoard";
import {DefaultBorad, guessFunc, correct_set, error_set, almost_set} from "./Word"
function guessing(word){
  if (word === "xxxxx"){
    return "Daisy"}
  return "xxxxx";
}
export const AppContext = createContext();
function App() {
  const [guess, setguess] = useState("xxxxx");
  const [board, setBoard] = useState(DefaultBorad);
  const [curAttempt, setCurrAttempt] = useState({attempt:0 ,letterPos :0})
  const [word, setWord] = useState({word:"DAISY"});
  const [correct, setCorrect] = useState(correct_set);
  const [almost, setAlmost] = useState(almost_set);
  const [error,setError] = useState(error_set);
  return (
    <div className="App">
      <nav>
        <h1>Dordle</h1>
        {/* <Keyboard/> */}
      </nav>
      <AppContext.Provider value ={{board, setBoard, curAttempt, setCurrAttempt, word, setWord, correct_set, setCorrect, almost_set, setAlmost, error_set,setError}} >
        <div className ="game">
      <Board/>
      <Keyboard/>
      </div>
      </AppContext.Provider>
      <button onClick={()=>setguess(guessing(guess))} >{guess}</button>
      </div>

  );
}
export default App;
